package com.microsoft.azure.sdk.iot.dps.device.privateapi.dpsparser;

import com.google.gson.Gson;
import com.google.gson.GsonBuilder;
import com.google.gson.JsonSyntaxException;
import com.google.gson.annotations.SerializedName;

public class DPSRegisterResponseTPMParser
{
    /*
    {"message":"Authorization required, resend request using supplied key","authenticationKey":"",
    "unencryptedAuthenticationKey":null,"keyName":"registration"}
     */

    private static final String MESSAGE = "message";
    @SerializedName(MESSAGE)
    private String message;

    private static final String AUTHENTICATION_KEY = "authenticationKey";
    @SerializedName(AUTHENTICATION_KEY)
    private String authenticationKey;

    private static final String UNENCRYPTED_AUTHENTICATION_KEY = "unencryptedAuthenticationKey";
    @SerializedName(UNENCRYPTED_AUTHENTICATION_KEY)
    private String unencryptedAuthenticationKey;

    private static final String KEY_NAME = "keyName";
    @SerializedName(KEY_NAME)
    private String keyName;

    private DPSRegisterResponseTPMParser()
    {
    }

    static public DPSRegisterResponseTPMParser createFromJson(String json) throws IllegalArgumentException
    {
        if((json == null) || json.isEmpty())
        {
            throw new IllegalArgumentException("Json is null or empty");
        }

        Gson gson = new GsonBuilder().disableHtmlEscaping().create();
        DPSRegisterResponseTPMParser dpsRegisterResponseTPMParser = null;

        try
        {
            dpsRegisterResponseTPMParser = gson.fromJson(json, DPSRegisterResponseTPMParser.class);
        }
        catch (JsonSyntaxException malformed)
        {
            throw new IllegalArgumentException("Malformed json:" + malformed);
        }

        return dpsRegisterResponseTPMParser;
    }

    public String getMessage()
    {
        return message;
    }

    public String getAuthenticationKey()
    {
        return authenticationKey;
    }

    public String getUnencryptedAuthenticationKey()
    {
        return unencryptedAuthenticationKey;
    }

    public String getKeyName()
    {
        return keyName;
    }
}
